'use client'
import {PublicKey} from '@solana/web3.js';
import {WalletReadyState} from './adapter';
import {BaseAdapter, Wallet} from './BaseAdapter';

interface PhantomWallet extends Wallet {
  isPhantom?: boolean;
}

interface PhantomWindow extends Window {
  phantom?: {
    solana?: PhantomWallet;
  };
  solana?: PhantomWallet;
}

declare const window: PhantomWindow;

export class PhantomWalletAdapter extends BaseAdapter {
  name: string = 'Phantom';
  url: string = 'https://phantom.app';
  icon =
    'data:image/svg+xml;base64,';


  constructor() {
    super();
  }

  async connect(): Promise<void> {
    try {
      if (this.connected || this.connecting) return;

      if (this.readyState === WalletReadyState.Loadable) {
        const url = encodeURIComponent(window.location.href);
        const ref = encodeURIComponent(window.location.origin);
        window.location.href = `https://phantom.app/ul/browse/${url}?ref=${ref}`;
        return;
      }

      this.connecting = true;

      const wallet = window.phantom?.solana || window.solana!;

      if (!wallet.isConnected) {
        try {
          await wallet.connect();
        } catch (error: any) {
          throw error;
        }
      }
      if (!wallet.publicKey) throw new Error('Account error');

      let publicKey: PublicKey;
      try {
        publicKey = new PublicKey(wallet.publicKey.toBytes());
      } catch (error: any) {
        throw error;
      }

      wallet.on('disconnect', this._disconnected);
      wallet.on('accountChanged', this._accountChanged);

      this.wallet = wallet;
      this.publicKey = publicKey;

      this.emit('connect', publicKey);
    } catch (error: any) {
      this.emit('error', error);
      throw error;
    } finally {
      this.connecting = false;
    }
  }
}

